mapper.select:
select uemail, uname, upass
from userList
where uemail = #{uemail}

mapper.update:
update userList
set uemail=#{uemail}, uname=#{uname}, upass=#{upass}
where uemail = #{uemail}

mapper.select:
select cname, cemail, cdomain, cpass
from recruiter
where cemail = #{cemail}

mapper.update:
update recruiter
set cname=#{cname}, cemail=#{cemail}, cdomain=#{cdomain}, cpass=#{cpass}
where cemail = #{cemail}
